package com.sapirgolan.myapplication.activity.Firbase;

import android.content.Context;

import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;
import com.sapirgolan.myapplication.activity.CallBack.CallBackQuestion;
import com.sapirgolan.myapplication.activity.object.Answer;
import com.sapirgolan.myapplication.activity.object.KindPets;
import com.sapirgolan.myapplication.activity.object.Question;

import java.util.ArrayList;

public class DataManager {
    private static DataManager dataManager = null;
    private ArrayList<KindPets> kindPets;
    private ArrayList<String> kindName;
    private Context context;
    private ArrayList<Question> questions;
    private Question question;
    private String questionByCategory;
    private ArrayList<Answer> answers;
    private final FirebaseDatabase firebaseDatabase;

    private ArrayList<Question> kindArrOrder;


    public void setQuestions(ArrayList<Question> questions) {
        this.questions = questions;
    }

    public ArrayList<Question> getQuestions() {
        return questions;
    }

    public DataManager setOneQuestion(Question question) {
        this.question = question;
        return this;
    }
    public ArrayList<Answer> getAnswers() {
        return answers;
    }

    public void setAnswers(ArrayList<Answer> answers) {
        this.answers = answers;
    }

    private CallBackQuestion callBackQuestion;

    public CallBackQuestion getCallBackQuestion() {
        return callBackQuestion;
    }

    public DataManager setCallBackQuestion(CallBackQuestion callBackQuestion) {
        this.callBackQuestion = callBackQuestion;
        return this;
    }

    private DataManager(Context context) {
        firebaseDatabase = FirebaseDatabase.getInstance();
        this.context = context;
        arrayOfCategory();
    }

    public static DataManager initData(Context context) {
        if (dataManager == null) {
            dataManager = new DataManager(context);
        }
        return dataManager;
    }


    public ArrayList<KindPets> getKindPets() {
        return kindPets;
    }

    public DataManager setQuestion(ArrayList<Question> questions) {
        this.questions = questions;
        return this;
    }

    public static DataManager getData() {
        return dataManager;
    }

    public ArrayList<String> getKindName() {
        return kindName;
    }


    public String getQuestionByCategory() {
        return questionByCategory;
    }

    public DataManager setQuestionByCategory(String questionByCategory) {
        this.questionByCategory = questionByCategory;
        return this;
    }


    private void arrayOfCategory() {
        questions = new ArrayList<Question>();
        kindName = new ArrayList<String>();
        kindPets = new ArrayList<>();
        KindPets cat = new KindPets();
        cat.setName("cat").setImageKind("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTzmY_vcCuXpUV3llyZliVs38Oy5nAYNAnAug&usqp=CAU");
        kindPets.add(cat);
        KindPets dog = new KindPets();
        dog.setName("dog").setImageKind("data:image/jpeg;base64,");
        kindPets.add(dog);
        KindPets fish = new KindPets();
        fish.setName("fish").setImageKind("data:image/jpeg;base64,");
        kindPets.add(fish);
        KindPets parrot = new KindPets();
        parrot.setName("parrot").setImageKind("data:image/jpeg;base64,");
        kindPets.add(parrot);
        KindPets rabbit = new KindPets();
        rabbit.setName("rabbit").setImageKind("data:image/jpeg;base64,");
        kindPets.add(rabbit);
        KindPets hamster = new KindPets();
        hamster.setName("hamster").setImageKind("data:image/jpeg;base64,");
        kindPets.add(hamster);

        for (int i = 0; i < kindPets.size(); i++) {
            kindName.add(kindPets.get(i).getName());
        }
    }

    public void addNewQuestion(Question question) {
        DatabaseReference myRef = firebaseDatabase.getReference("question").child(question.getIdQ());
//                getReference("category").child(FirebaseAuth.getInstance().
//                        getCurrentUser().getUid()).
//                child(question.getCategory()).child(question.getIdQ());
        myRef.child("title").setValue(question.getTitle());
        myRef.child("category").setValue(question.getCategory());
        myRef.child("text").setValue(question.getText());
        //myRef.child("answer").setValue(question.getAnswers());
    }

    public ArrayList<Question> getKindArrOrder() {
        return kindArrOrder;
    }

    public void setKindArrOrder(ArrayList<Question> kindArrOrder) {
        this.kindArrOrder = kindArrOrder;
    }
}
